import React, { useState } from "react";

export const Crud = () => {
  const [tasks, setTasks] = useState([]);
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [edit, setEdit] = useState(false);

const addTask = () => {
    setTasks([...{title, description}]) // Los tres puntos permiten la propagación, es decir
}

  const actions = (e) => {
      e.preventDefault();
      addTask()
  }

  return (
    <div className="container">
      <div className="mt-2 justify-content-center d-flex">
        <div className="col-6">
            <div className="card">
              <h3 className="card-title text-center">CRUD</h3>
              <div className="card-body">
                <form onSubmit = {actions}>
                  <div className="mb-3">
                    <input
                      type="text"
                      id="title"
                      placeholder="Ingrese Título"
                      value={title}
                      required
                      autofocus
                      className="form-control"
                      onChange = {e => setTitle(e.target.value)} // e es el evento. Puedo usar cualquier otro nombre.
                    />
                  </div>
                  <div className="mb-3">
                    <input
                      type="text"
                      id="description"
                      placeholder="Ingrese Descripción"
                      value={description}
                      required
                      className="form-control"
                      onChange = {e => setDescription(e.target.value)}
                    />
                  </div>
                  <button className="btn btn-primary form-control" type="submit">Guardar</button>
                </form>
              </div>
            </div>
        </div>
      </div>
    </div>
  );
};
